      <FormStyle onSubmit={submitHandler}>


        <FaSearch />
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
        /> 


         <Button text="Search" onClick={submitHandler} />


    </FormStyle> 


  )
}
