    border: none;
    background: linear-gradient(35deg, #494949, #313131);
    font-size: 2rem;
    color: white;
    padding: 1rem 3rem;
    border-radius: 1rem;
    outline: none;
    height: 80%;
    width: 80%;
    opacity: 0.35;

  }

  svg {
    position: relative;
    top: 50%;
    left: 3% ;
    transform: translate(0%, 0%);
    color: black;

  }
`;

export default Search;
